import React, { useState } from 'react';
import './index.css';

const App = () => {

    const [item,setItem] = useState("");
    
    const inputItem = (event) =>{
        setItem(event.target.value);


    }
    return(
    <>
      <div className="main_div">
          <div className="center_div">
              <h1>ToDo List </h1>
              <br/>
              <input type="text" placeholder="Add an item" onChange={inputItem}></input>
              <button>+</button>
              <ol>
                  <li>{item}</li>
              </ol>
          </div>
      </div>    
    </>
    );
}

export default App;
